mapper.select:
 select nvl((
    select condition_id as conditionId
    from fitfusion_conditions
    where user_id = #{userId}
), 0)
 from dual

mapper.insert:
insert into FITFUSION_CONDITIONS (
                          condition_id
                        , user_id
                        , condition_level
                        , created_date
            ) values (
                          #{conditionId}
                        , #{userId}
                        , #{conditionLevel}
                        , sysdate
                 )
