mapper.insert:
insert into FITFUSION_USER_GOALS (
    goal_id
  , goal_name
  , goal_desc
  , start_weight
  , target_weight
  , start_date
  , end_date
  , user_id
  , progress_id
) values (
  EXERCISE_GOAL_SEQ.NEXTVAL
  , #{goalType}
  , #{goalDescription}
  , #{startWeight}
  , #{targetWeight}
  , #{startDate}
  , #{endDate}
  , #{userId}
  , #{progressId}
)

mapper.update:
update
    FITFUSION_USER_GOALS
set
    GOAL_NAME = #{goalType}
  , GOAL_DESC = #{goalDescription}
  , START_WEIGHT = #{startWeight}
  , TARGET_WEIGHT = #{targetWeight}
  , START_DATE = #{startDate}
  , END_DATE = #{endDate}
where
    GOAL_ID = #{goalId}
