mapper.insert:
insert into fitfusion_exercise_logs (
    log_id
  , log_date
  , sets
  , reps
  , duration_minutes
  , is_checked
  , user_id
  , exercise_id
  , routine_exercise_id
  ) values (
    seq_exercise_log.nextval
  , sysdate
  , #{sets}
  , #{reps}
  , #{durationMinutes}
  , #{isChecked}
  , #{userId}
  , #{exerciseId}
  , #{routineExerciseId}
)
