mapper.select:
select count(*)
from fitfusion_routine
where user_id = #{userId}

mapper.insert:
insert into FITFUSION_ROUTINE(
        routine_id
      , user_id
      , name
      , difficulty_level
      , created_date
      , updated_date
      , description)
values (
        #{routineId}
      , #{userId}
    , #{name}
    , #{difficultyLevel}
    , sysdate
    , sysdate
    , #{description}
       )

mapper.update:
update fitfusion_routine
set 
    name = #{name}
  , difficulty_level = #{difficultyLevel}
  , updated_date = sysdate
  , description = #{description}
where user_id = #{userId} and routine_id = #{routineId}
